using System.Text;using System.Windows;using System.Windows.Controls;using System.Windows.Data;using System.Windows.Documents;using System.Windows.Input;using System.Windows.Media;using System.Windows.Media.Imaging;using System.Windows.Navigation;using System.Windows.Shapes;namespace ColourSwapper{    /// <summary>    /// Interaction logic for MainWindow.xaml    /// </summary>    public partial class MainWindow : Window    {        public MainWindow()        {            InitializeComponent();        }        private void BlueClick(object sender, RoutedEventArgs e)        {            btnClrSwp.Background = Brushes.Blue;            if (btnClrSwp.Background == Brushes.Blue)            {                btnClrSwp.Content = "DABADEDABADA";            }            MessageBox.Show("DABADEEYA!");        }        private void ResetColorClick(object sender, RoutedEventArgs e)        {            if (btnClrSwp.Background == Brushes.Blue)            {                btnClrSwp.Background = Brushes.Teal;                btnClrSwp.Content = "I'm blue!";                double blueBtnSquareSize = 150;                btnClrSwp.Width = blueBtnSquareSize;                btnClrSwp.Height = blueBtnSquareSize;            }        }    }}